sys_variable_value.value:
(function execute(inputs, outputs) {
  var time_off_type = inputs.type;
  var duration = inputs.duration;

  var remaining_pto = inputs.pto;
  var used_aoe = inputs.aoe;
  var used_sick_leave = inputs.sick_leave;

  outputs.remaining_pto = remaining_pto;
  outputs.used_aoe = used_aoe;
  outputs.used_sick_leave = used_sick_leave;

  if (time_off_type == "pto") {
    outputs.remaining_pto = Number(remaining_pto) - Number(duration);
  } else if (time_off_type == "aoe") {
  	outputs.used_aoe = Number(used_aoe) + Number(duration);
  } else if (time_off_type == "sick_leave") {
  	outputs.used_sick_leave = Number(used_sick_leave) + Number(duration);
  }

})(inputs, outputs);

