sys_script.script:
(function executeRule(current, previous) {
	var employeeGr = new GlideRecord("x_1122522_pfc_employee_info");
	employeeGr.addQuery("user", current.requested_by);
	employeeGr.query();

	var fromDate = new GlideDateTime(current.from);
	var toDate = new GlideDateTime(current.to);

	var durationInMilliSeconds = toDate.getNumericValue() - fromDate.getNumericValue();
	var durationInDays = (durationInMilliSeconds / (1000 * 3600 * 24));

	gs.addInfoMessage("Duration in days: " + durationInDays);


	if (employeeGr.next()) {
		var remainingPto = employeeGr.getValue("remaining_pto");
		if (remainingPto < durationInDays) {
			gs.addErrorMessage("You don't have enough available remaining PTO");
			current.setAbortAction(true);
		}

		current.setValue("time_off_duration", durationInDays);
	} else {
		gs.addErrorMessage("There is no employee info present");
		current.setAbortAction(true);
	}
})(current, previous);
