sys_script_client.script:
function onChange() {
	g_form.addInfoMessage("works");
	if (g_user.userID == g_form.getValue("user")) {
		var GetIPAddress = new GlideAjax("GetUserIP");
		GetIPAddress.addParam("sysparm_name", "getIpAddress");
		GetIPAddress.getXML(ipFromScriptInclude);
	} else {
		g_form.clearValue("ip_address");
	}

    function ipFromScriptInclude(response) {
        var ipFromScriptInclude = response.responseXML.documentElement.getAttribute("answer");
        g_form.setReadOnly("ip_address", true);
        g_form.clearValue("ip_address");
        g_form.setValue("ip_address", ipFromScriptInclude);
    }
}
